function fontPicker(props) {
    const [name, setName] = useState("");

    const handleSubmit = (evt) => {
        evt.preventDefault();
        alert(`Submitting Name ${name}`)
    }


    return <div className="fontPicker">
        <form>
            <label>

                <input
                    type="text"

                    onChange={e => props.setFont(e.target.value)}
                />
                Font Name
      </label>

        </form>
    </div>
}

export default fontPicker
