import YesButton from "../components/yesbutton.js";
import ColorPicker from "../components/colorpicker.js";
import React, { useState, useEffect } from 'react';
import htmlToImage from 'html-to-image';
import download from 'downloadjs';

function HomePage() {
    const [bgColor, setBgColor] = useState({
        color: {
            r: '241',
            g: '112',
            b: '19',
            a: '1',
        },
    });

    function useSave() {
        // useEffect(() => {
        htmlToImage.toBlob(document.getElementById('yes-button'))
            .then(function (blob) {
                download(blob, 'my-node.png');
            });
        // });
    }


    return <div>
        <YesButton bgColor={bgColor} />
        <div> Background Color </div>
        <ColorPicker setBgColor={setBgColor} />
        <button onClick={() => useSave()}>Download</button>
    </div >
}

export default HomePage
