import { motion } from "framer-motion"
import styled from 'styled-components';
import {
    BrowserView,
    MobileView,
    isBrowser,
    isMobile
} from "react-device-detect";

function YesButton(props) {
    console.log(isMobile ? 600 : 300);
    // alert(isMobile);
    const w = 300;
    const h = w / 2.5;
    const b = w / 300;
    const s = w / 100;

    console.log(w);

    const topColor = `rgba(${props.topColor.color.r}, ${props.topColor.color.g}, ${props.topColor.color.b}, ${props.topColor.color.a})`;
    var shadowColor = `rgba(${props.topColor.color.r}, ${props.topColor.color.g}, ${props.topColor.color.b}, ${props.topColor.color.a / 2})`;
    shadowColor = `rgba(0,0,0,0.2)`;
    const btnStyle = {
        width: `${w}px`,
        height: `${h}px`,
        // transform: 'scale(0.5)',
        // transformOrigin: 'left',
        textAlign: 'center',
        // owmargin: '10px',

        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: `${h / 8}px`,
        // boxShadow: `5px 5px ${shadowColor} `,
        border: `${b}px solid rgba(${props.borderColor.color.r}, ${props.borderColor.color.g}, ${props.borderColor.color.b}, ${props.borderColor.color.a})`,
        // borderStyle: 'dashed',
        background: `${topColor}`,
        backgroundImage: `linear-gradient(rgba(${props.topColor.color.r}, ${props.topColor.color.g}, ${props.topColor.color.b}, ${props.topColor.color.a}), 
        rgba(${props.bottomColor.color.r}, ${props.bottomColor.color.g}, ${props.bottomColor.color.b}, ${props.bottomColor.color.a})`,
    }

    const textStyle = {
        fontSize: `${h / 2.5}pt`,
        fontFamily: `'${props.font}'`,
        // paintOrder: 'stroke fill',
        position: 'relative',
        top: '-5.5%',
        right: '-2%',
        color: `rgba(${props.color.color.r}, ${props.color.color.g}, ${props.color.color.b}, ${props.color.color.a})`,
        WebkitTextStrokeWidth: `${b}px`,
        WebkitTextStrokeColor: `rgba(${props.textOutlineColor.color.r}, ${props.textOutlineColor.color.g}, ${props.textOutlineColor.color.b}, ${props.textOutlineColor.color.a})`,

    }

    const gridStyle = {
        display: 'grid',
        gridTemplateColumns: 'auto auto auto auto auto auto auto auto auto auto',
        position: 'absolute',
        width: `${w + b * 2}px`,
        height: `${h + b * 2}px`,
        zIndex: '-1',
    }

    const gridBlock = {
        // outline: ' 0.5px solid rgba(240,240,240,1)',
        borderTop: '1px solid rgba(0,0,0,0.2)',
        borderRight: '1px solid rgba(0,0,0,0.2)',
        // background: 'white',
    }

    const gridCircle = {
        border: ' 1px dashed rgba(50,50,50,1)',

        borderRadius: `${h / 8}px`,

    }

    const containerStyle = {
        width: `${w}px`,
        height: `${h}px`,
        position: 'relative',
        borderRadius: `${h / 8}px`,
        cursor: 'pointer',
        x: -s, y: -s, boxShadow: `${s}px ${s}px 0 ${shadowColor}`,
        userSelect: 'none',
        margin: `${s * 3}px`,
        // width: '100%',
        // height: '100%',
    }

    const supStyle = {
        // color: 'black',
    }

    const captureStyle = {
        // width: `${w * 2}px`,
        // height: `${h * 2}px`,
        // display: 'flex',
        // alignItems: 'center',
        // justifyContent: 'center',
    }

    return <div id="yes-button" style={captureStyle}>
        <motion.div
            id="yes-button"
            style={containerStyle}
            animate={{ x: -s, y: -s, boxShadow: `${s}px ${s}px 0 ${shadowColor}`, }}
            whileHover={{ x: -s * 2, y: -s * 2, boxShadow: `${s * 2}px ${s * 2}px 0 ${shadowColor}`, }}
            whileTap={{ x: 0, y: 0, boxShadow: `0px 0px 0 ${shadowColor}`, }}>
            <div style={gridStyle}>
                <div style={gridCircle}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridCircle}></div>

                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>

                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>

                <div style={gridCircle}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridBlock}></div>
                <div style={gridCircle}></div>
            </div>

            <div style={btnStyle} >
                <span style={textStyle}>yes<span style={supStyle}>Â®</span></span>

            </div>
        </motion.div>
    </div>
}

export default YesButton
