import YesButton from "../components/yesbutton.js";
import ColorPicker from "../components/colorpicker.js";
import React, { useState, useEffect } from 'react';
import htmlToImage from 'html-to-image';
import download from 'downloadjs';

function HomePage() {
    const [topColor, setTopColor] = useState({
        color: {
            r: '241',
            g: '112',
            b: '255',
            a: '1',
        },
    });
    const [bottomColor, setBottomColor] = useState({
        color: {
            r: '241',
            g: '112',
            b: '255',
            a: '1',
        },
    });
    const [color, setColor] = useState({
        color: {
            r: '0',
            g: '0',
            b: '0',
            a: '1',
        },
    });
    const [borderColor, setBorderColor] = useState({
        color: {
            r: '0',
            g: '0',
            b: '0',
            a: '1',
        },
    });



    function useSave() {
        // useEffect(() => {
        htmlToImage.toBlob(document.getElementById('yes-button'), { width: '500', height: '200' })
            .then(function (blob) {
                download(blob, 'yes®.png');
            });
        // });
    }


    return <div>
        <YesButton topColor={topColor} bottomColor={bottomColor} color={color} borderColor={borderColor} />
        <div> Top Color </div>
        <ColorPicker color={topColor} setColor={setTopColor} />
        <div> Bottom Color </div>
        <ColorPicker color={bottomColor} setColor={setBottomColor} />
        <div>Color </div>
        <ColorPicker color={color} setColor={setColor} />
        <div>Border Color </div>
        <ColorPicker color={borderColor} setColor={setBorderColor} />
        <button onClick={() => useSave()}>Download</button>
    </div >
}

export default HomePage
