        display: 'grid',
        gridTemplateColumns: 'auto auto auto auto auto auto auto auto auto auto',
        position: 'absolute',
        width: `${w + b * 2}px`,
        height: `${h + b * 2}px`,
        zIndex: '-1',
    }

    const gridBlock = {
        // outline: ' 0.5px solid rgba(240,240,240,1)',
        borderTop: '1px solid rgba(0,0,0,0.2)',
        borderRight: '1px solid rgba(0,0,0,0.2)',
        // background: 'white',
    }

    const gridCircle = {
        border: ' 1px dashed rgba(50,50,50,1)',

        borderRadius: `${h / 8}px`,

    }

    const containerStyle = {
        position: 'relative',
        borderRadius: '15px',
        cursor: 'pointer',
        x: -3, y: -3, boxShadow: `3px 3px 0 ${shadowColor}`,
        userSelect: 'none',
        margin: '4px',
        // width: '100%',
        // height: '100%',
    }

    const supStyle = {
        // color: 'black',
    }

