        <motion.div
            id="yes-button"
            style={containerStyle}
            animate={{ x: -4, y: -4, boxShadow: `4px 4px 0 ${shadowColor}`, }}
            whileHover={{ x: -6, y: -6, boxShadow: `6px 6px 0 ${shadowColor}`, }}
            whileTap={{ x: 0, y: 0, boxShadow: `0px 0px 0 ${shadowColor}`, }}>
