import YesButton from "../components/yesbutton.js";
import ColorPicker from "../components/colorpicker.js";
import FontPicker from "../components/fontPicker";
import cycleStates from "../components/cycleStates.js"
import React, { useState, useEffect } from 'react';
import htmlToImage from 'html-to-image';
import download from 'downloadjs';
import { saveAs } from 'file-saver';
// import domtoimage from 'dom-to-image';
import { Icon } from 'antd';
import innerHeight from 'ios-inner-height';


import { motion } from "framer-motion"
var i = 1;

function HomePage() {
    const [font, setFont] = useState("NowTextBold");
    const [strokeWeight, setStrokeWeight] = useState(1);
    const [sizeMultiplier, setSizeMultiplier] = useState(3);
    const [shadow, setShadow] = useState(true);
    const [clicked, setClicked] = useState(false);
    const [isLockup, setLockup] = useState(false);
    const [topColor, setTopColor] = useState({

        r: '255',
        g: '255',
        b: '255',
        a: '0.3',

    });
    const [bottomColor, setBottomColor] = useState({

        r: '255',
        g: '255',
        b: '255',
        a: '0.3',

    });

    function setBothColors(json) {
        setTopColor(json);
        setBottomColor(json);
    }



    function playSequence() {
        if (i < 3) {
            setTopColor(cycleStates[i].topColor);
            setBottomColor(cycleStates[i].bottomColor);
            setColor(cycleStates[i].color);

        } else {
            setRandomColor()
        }
        i = i + 1;
        if (i > 10) {
            i = 0;
        }

    }

    function resetColor() {
        setBothColors({
            r: 255,
            g: 255,
            b: 255,
            a: 0.3,

        })
        setColor({
            r: 255,
            g: 255,
            b: 255,
            a: 1,

        })

    }

    function setRandomColor() {
        setBothColors({
            r: Math.floor(Math.random() * 255),
            g: Math.floor(Math.random() * 255),
            b: Math.floor(Math.random() * 255),
            a: '1',
        })
        setClicked(!clicked);
    }

    const [color, setColor] = useState({
        r: '255',
        g: '255',
        b: '255',
        a: '1',
    });

    const [textOutlineColor, setTextOutlineColor] = useState({
        color: {
            r: '0',
            g: '0',
            b: '0',
            a: '1',
        },
    });

    const [borderColor, setBorderColor] = useState({
        color: {
            r: '0',
            g: '0',
            b: '0',
            a: '1',
        },
    });




    function useSave() {

        htmlToImage.toBlob(document.getElementById('stamp'), {})
            .then(function (blob) {
                download(blob, 'yes速.png');
            });

        // domtoimage.toBlob(document.getElementById('stamp'))
        //     .then(function (blob) {
        //         window.saveAs(blob, 'yes速.png');
        //     });



        // htmlToImage.toCanvas(document.getElementById('stamp'))
        //     .then(function (canvas) {
        //         document.body.appendChild(canvas);
        //     });
    }



    const variants = {
        hover: {
            scale: 1.05
        },
        tap: {
            scale: 0.95
        }
    }

    console.log(innerHeight);


    return <div>

        <div style={{ height: `${innerHeight}px` }} className="main">
            <div className="canvas">
                {isLockup ? (
                    <div id="stamp"
                        className="stampLockup"
                        style={{
                            borderWidth: `${strokeWeight}px`,
                            width: `${100 * sizeMultiplier + 35}px`,
                            borderRadius: `${(100 * sizeMultiplier + 35) / 2.5 / 8}px`,
                            fontSize: `${sizeMultiplier * 5}pt`,
                            padding: `20px 12px 0px 10px`,
                        }}>
                        <div className="stamp-title">The Open Banking Ecosystem</div>
                        <YesButton shadow={shadow} sizeMultiplier={sizeMultiplier} strokeWeight={strokeWeight} clicked={clicked} font={font} topColor={topColor} textOutlineColor={textOutlineColor} bottomColor={bottomColor} color={color} borderColor={borderColor} />
                    </div>
                ) : (
                        < div id="stamp" className="stampButton">
                            <YesButton shadow={shadow} sizeMultiplier={sizeMultiplier} strokeWeight={strokeWeight} clicked={clicked} font={font} topColor={topColor} textOutlineColor={textOutlineColor} bottomColor={bottomColor} color={color} borderColor={borderColor} />
                        </div>
                    )}
            </div>
            <motion.div className="control">
                <motion.div className="controlStrip">
                    <ColorPicker className="colorPicker" color={topColor} setColor={setBothColors} />
                    Background

            </motion.div>
                {/* <motion.div className="controlStrip">
                    <ColorPicker className="colorPicker" color={bottomColor} setColor={setBottomColor} />
                    Gradient

            </motion.div> */}
                <motion.div className="controlStrip">
                    <ColorPicker className="colorPicker" color={color} setColor={setColor} />
                    Typography
            </motion.div>
                <motion.div variants={variants} whileHover="hover" whileTap="tap" className="controlStrip" onClick={() => playSequence()} ><Icon type="forward" /> Cycle</motion.div>
                <motion.div variants={variants} whileHover="hover" whileTap="tap" className="controlStrip" onClick={() => resetColor()} ><Icon type="rollback" /> Reset</motion.div>
                <motion.div variants={variants} whileHover="hover" whileTap="tap" className="controlStrip" onClick={() => setStrokeWeight((strokeWeight + 1) % 3 + 1)}><Icon type="edit" /> Stroke Weight</motion.div>
                <motion.div variants={variants} whileHover="hover" whileTap="tap" className="controlStrip" onClick={() => setSizeMultiplier((sizeMultiplier + 1) % 10 + 1)}><Icon type="fullscreen" /> Size</motion.div>
                <motion.div variants={variants} whileHover="hover" whileTap="tap" className="controlStrip" onClick={() => setShadow(!shadow)}><Icon type="border-top" /> Shadow</motion.div>
                <motion.div variants={variants} whileHover="hover" whileTap="tap" className="controlStrip" onClick={() => setLockup(!isLockup)}><Icon type="border-outer" /> Lockup</motion.div>
                <motion.div variants={variants} whileHover="hover" whileTap="tap" className="controlStrip" onClick={() => useSave()}><Icon type="download" /> Download PNG</motion.div>
                {/* <ColorPicker className="colorPicker" color={topColor} setColor={setTopColor} />
            <div className="label"> Top Color </div>

            <ColorPicker className="colorPicker" color={bottomColor} setColor={setBottomColor} />
            <div className="label"> Bottom Color </div>

            <ColorPicker className="colorPicker" color={color} setColor={setColor} />
            <div className="label">Text Color </div>

            <ColorPicker className="colorPicker" color={textOutlineColor} setColor={setTextOutlineColor} />
            <div className="label">Text Outline Color </div>

            <ColorPicker className="colorPicker" color={borderColor} setColor={setBorderColor} />
            <div className="label">Border Color </div>

            <FontPicker setFont={setFont} /> */}



                {/* <button onClick={() => useSave()}>Download PNG</button> */}
            </motion.div>
        </div>
    </div >
}

export default HomePage
