import YesButton from "../components/yesbutton.js";
import ColorPicker from "../components/colorpicker.js";
import React, { useState, useEffect } from 'react';
import htmlToImage from 'html-to-image';
import download from 'downloadjs';

function HomePage() {
    const [topColor, setTopColor] = useState({
        color: {
            r: '241',
            g: '112',
            b: '255',
            a: '1',
        },
    });
    const [bottomColor, setBottomColor] = useState({
        color: {
            r: '241',
            g: '112',
            b: '255',
            a: '1',
        },
    });
    const [color, setColor] = useState({
        color: {
            r: '241',
            g: '112',
            b: '255',
            a: '1',
        },
    });



    function useSave() {
        // useEffect(() => {
        htmlToImage.toBlob(document.getElementById('yes-button'))
            .then(function (blob) {
                download(blob, 'my-node.png');
            });
        // });
    }


    return <div>
        <YesButton topColor={topColor} color={color} />
        <div> Top Color </div>
        <ColorPicker setColor={setTopColor} />
        <div> Bottom Color </div>
        <ColorPicker setBottomColor={setBottomColor} />
        <div>Color </div>
        <ColorPicker setColor={setColor} />
        <button onClick={() => useSave()}>Download</button>
    </div >
}

export default HomePage
