import React, { useState, useEffect } from 'react';


import { motion } from "framer-motion"


function TestPage() {

    const [bgColor, setBgColor] = useState('red');

    const testStyle = {
        background: 'blue',
        width: '100px',
        height: '100px',
        borderRadius: '15px',
    }

    const variant = {
        click: {
            background: bgColor,
            scale: [1.1, 0.9, 1]
        }
    }


    return <div>

        < motion.div style={testStyle} variants={variant} animate="click" />

    </div >
}

export default TestPage
