import { motion } from "framer-motion"


function YesButton(props) {
    console.log(props.topColor);
    const btnStyle = {
        width: '500px',
        height: '200px',
        // transform: 'scale(0.5)',
        // transformOrigin: 'left',
        textAlign: 'center',

        lineHeight: '95px',
        borderRadius: '12.5px',

        border: `1px solid rgba(${props.borderColor.color.r}, ${props.borderColor.color.g}, ${props.borderColor.color.b}, ${props.borderColor.color.a})`,

        background: `rgba(${props.topColor.color.r}, ${props.topColor.color.g}, ${props.topColor.color.b}, ${props.topColor.color.a})`,
        backgroundImage: `linear-gradient(rgba(${props.topColor.color.r}, ${props.topColor.color.g}, ${props.topColor.color.b}, ${props.topColor.color.a}), 
        rgba(${props.bottomColor.color.r}, ${props.bottomColor.color.g}, ${props.bottomColor.color.b}, ${props.bottomColor.color.a})`,
    }

    const textStyle = {
        fontSize: '40pt',
        fontFamily: `${props.font}`,
        webkitTextStrokeWidth: '1px',
        webkitTextStrokeColor: `rgba(${props.textOutlineColor.color.r}, ${props.textOutlineColor.color.g}, ${props.textOutlineColor.color.b}, ${props.textOutlineColor.color.a})`,
        color: `rgba(${props.color.color.r}, ${props.color.color.g}, ${props.color.color.b}, ${props.color.color.a})`,
    }

    const supStyle = {
        color: 'black',
    }

    return <div id="yes-button" style={btnStyle} className="yesButton"><span style={texStyle}>yes<sup style={supStyle}>Â®</sup></span></div>
}

export default YesButton
